package chapter02;

// Coding By 김용선.

public class Hello {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 화면에 문자를 출력.
		System.out.println("안녕!");
		
		// 2개의 문자열을 합쳐서 출력.
		System.out.println("안녕" + "!");
		
		// 문자열을 hello 변수에 대입 후, hello 변수 출력
		String hello = "안녕!";
		System.out.println(hello);
	}

}
